class Solution {
public:
    ListNode* Merge(ListNode* pHead1, ListNode* pHead2) {
        // 使用辅助头结点dummy
        // 依次遍历并移动
        // 最终需要判断是否有链表没有遍历完整的情况，将后续结点全部添加
        ListNode dummy(-1);
        ListNode* p = &dummy;

        while (pHead1 != nullptr && pHead2 != nullptr) {
            if(pHead1->val val){
                p->next = pHead1;
                pHead1 = pHead1->next;
            }else{
                p->next = pHead2;
