/**
 * struct ListNode {
 *  int val;
 *  struct ListNode *next;
 *  ListNode(int x) : val(x), next(nullptr) {}
 * };
 */
#include 
class Solution {
  public:
    ListNode* mergeKLists(vector& lists) {
        if(lists.empty()) return nullptr;
        return mergeKListsHelper(lists, 0, lists.size() - 1);
    }

    // 分治，拆分为两两一组，然后递归合并
    // left、right相等时返回单个列表
    // 否则返回两两合并后的结果

    ListNode* mergeKListsHelper(vector& lists, int left, int right) {
        if (left == right) {
            return lists[left];
        }
        int m = left + (right - left) / 2;
        ListNode* l = mergeKListsHelper(lists, left, m);
        ListNode* r = mergeKListsHelper(lists, m + 1, right);
        return merge2List(l, r);
    }

    // 初始化一个dummy结点作为头结点
    ListNode* merge2List(ListNode* l1, ListNode* l2){
        ListNode dummy(-1);
        ListNode* l = &dummy;
        while (l1 && l2) {
            if(l1->val > l2->val){
                l->next = l2;
                l2 = l2->next;
            }else{
                l->next = l1;
                l1 = l1->next;
            }
            l = l->next;
        }
        if(!l1){
            l->next = l2;
        }else{
            l->next = l1;
        }
        return dummy.next;
    }
};

