/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode(int x) : val(x), next(NULL) {}
 * };
 */
class Solution {
public:
    // 使用快慢指针
    // fast指针到链表末尾时 或 快慢指针相遇时，跳出循环
    // 循环外判断是哪种跳出情况
    bool hasCycle(ListNode *head) {
        if(!head || !head->next){
            return false;;
        }

        ListNode* slow = head, *fast = head;
        while (fast != nullptr && fast->next != nullptr) {
            slow = slow->next;
            fast = fast->next->next;
            if(fast == slow){
                break;
            }
        }
        if(fast == slow){
            return true;
        }
        return false;    
    }
};

