/*
struct ListNode {
    int val;
    struct ListNode *next;
    ListNode(int x) :
        val(x), next(NULL) {
    }
};
*/
#include 
class Solution {
public:
    // 如果存在环的话，将一个指针从相遇点移到head处，同时前进即为入口
    // 推导：Case 1 环很大：head到入口处为a，入口到相遇为b，相遇到入口为c
    // slow前进了a+b，fast前进了a+b+c+b，因为fast是slow的2倍
    // 所以a=c，也就是相遇到入口和head到入口的距离相等
    // 环很小的情况时，fast前进a+b+m(b+c)，slow前进a+b+n(b+c)
    // 所以a+b=(m-2n)(b+c)，相当于一个在环外走一个在环内走，走了m-2n-1时和上种情况相同

    ListNode* EntryNodeOfLoop(ListNode* pHead) {
        if(!pHead || !pHead->next){
            return nullptr;
        }

        ListNode* slow = pHead, *fast = pHead;
        while (fast != nullptr && fast->next != nullptr) {
            slow = slow->next;
            fast = fast->next->next;
            if(slow == fast){
                break;
            }
        }

        if(slow != fast){
            return nullptr;
        }

        slow = pHead;
        while (slow != fast) {
            slow = slow->next;
            fast = fast->next;
        }
        return slow;

    }
};

