class Solution {
public:
    // 保证两个指针走过的长度相同即可
    // 到达空结点时重置到另一个链表头结点处，否则前进（不要重复前进，不然快一步）
    // 如果相交，相遇处就是相交结点。否则在nullptr处相遇。
    ListNode* FindFirstCommonNode(ListNode* pHead1, ListNode* pHead2) {
        ListNode* p1 = pHead1;
        ListNode* p2 = pHead2;
        while (p1 != p2) {
            if(p1 == nullptr) p1 = pHead2;
            else p1 = p1->next;
            if(p2 == nullptr) p2 = pHead1;
            else p2 = p2->next;
        }
        return p1;
    }
};
